span><span class="l"><a href="#620">620: </a></span><span class="l"><a href="#621">621: </a></span><span class="l"><a href="#622">622: </a></span><span class="l"><a href="#623">623: </a></span><span class="l"><a href="#624">624: </a></span></span><span class="l"><a href="#625">625: </a></span></span><span class="l"><a href="#626">626: </a></span></span><span class="l"><a href="#627">627: </a></span></span><span class="l"><a href="#628">628: </a></span><span class="l"><a href="#629">629: </a></span><span class="l"><a href="#630">630: </a></span><span class="l"><a href="#631">631: </a></span><span class="l"><a href="#632">632: </a></span><span class="l"><a href="#633">633: </a></span><span class="l"><a href="#634">634: </a></span><span class="l"><a href="#635">635: </a></span><span class="l"><a href="#636">636: </a></span><span class="l"><a href="#637">637: </a></span><span class="l"><a href="#638">638: </a></span><span class="l"><a href="#639">639: </a></span><span class="l"><a href="#640">640: </a></span><span class="l"><a href="#641">641: </a></span><span class="l"><a href="#642">642: </a></span><span class="l"><a href="#643">643: </a></span><span class="l"><a href="#644">644: </a></span><span class="l"><a href="#645">645: </a></span><span class="l"><a href="#646">646: </a></span><span class="l"><a href="#647">647: </a></span><span class="l"><a href="#648">648: </a></span><span class="l"><a href="#649">649: </a></span><span class="l"><a href="#650">650: </a></span><span class="l"><a href="#651">651: </a></span><span class="l"><a href="#652">652: </a></span><span class="l"><a href="#653">653: </a></span><span class="l"><a href="#654">654: </a></span><span class="l"><a href="#655">655: </a></span><span class="l"><a href="#656">656: </a></span><span class="l"><a href="#657">657: </a></span><span class="l"><a href="#658">658: </a></span><span class="l"><a href="#659">659: </a></span><span class="l"><a href="#660">660: </a></span><span class="l"><a href="#661">661: </a></span><span class="l"><a href="#662">662: </a></span><span class="l"><a href="#663">663: </a></span></code></pre>
	            <pre class="code"><code><span id="1" class="l"><span class="xlang">&lt;?php</span>
</span><span id="2" class="l"><span class="php-comment">/**
</span></span><span id="3" class="l"><span class="php-comment"> * This class offers a simple way to transform an absolute or relative link
</span></span><span id="4" class="l"><span class="php-comment"> * to a relative one to the website root.
</span></span><span id="5" class="l"><span class="php-comment"> * It can also deals with absolute url and will convert only those from this
</span></span><span id="6" class="l"><span class="php-comment"> * site into relatives ones.
</span></span><span id="7" class="l"><span class="php-comment"> * Usage :
</span></span><span id="8" class="l"><span class="php-comment"> * &lt;ul&gt;
</span></span><span id="9" class="l"><span class="php-comment"> *   &lt;li&gt;In content, get the url with the absolute() method. It will allow content include at multiple level&lt;/li&gt;
</span></span><span id="10" class="l"><span class="php-comment"> *   &lt;li&gt;In forms, get the url with the relative() method. It's a faster way to display url&lt;/li&gt;
</span></span><span id="11" class="l"><span class="php-comment"> * &lt;/ul&gt;
</span></span><span id="12" class="l"><span class="php-comment"> * @package     Util
</span></span><span id="13" class="l"><span class="php-comment"> * @copyright   &amp;copy; 2005-2019 PHPBoost
</span></span><span id="14" class="l"><span class="php-comment"> * @license     https://www.gnu.org/licenses/gpl-3.0.html GNU/GPL-3.0
</span></span><span id="15" class="l"><span class="php-comment"> * @author      Loic ROUCHON &lt;horn@phpboost.com&gt;
</span></span><span id="16" class="l"><span class="php-comment"> * @version     PHPBoost 5.2 - last update: 2019 01 22
</span></span><span id="17" class="l"><span class="php-comment"> * @since       PHPBoost 2.0 - 2009 01 14
</span></span><span id="18" class="l"><span class="php-comment"> * @contributor Julien BRISWALTER &lt;j1.seth@phpboost.com&gt;
</span></span><span id="19" class="l"><span class="php-comment"> * @contributor Arnaud GENET &lt;elenwii@phpboost.com&gt;
</span></span><span id="20" class="l"><span class="php-comment"> * @contributor mipel &lt;mipel@phpboost.com&gt;
</span></span><span id="21" class="l"><span class="php-comment"> * @contributor janus57 &lt;janus57@janus57.fr&gt;
</span></span><span id="22" class="l"><span class="php-comment">*/</span>
</span><span id="23" class="l">
</span><span id="24" class="l"><span class="php-keyword2">define</span>(<span class="php-quote">'SERVER_URL'</span>, <span class="php-var">$_SERVER</span>[<span class="php-quote">'PHP_SELF'</span>]);
</span><span id="25" class="l">
</span><span id="26" class="l"><span class="php-keyword1">class</span> Url
</span><span id="27" class="l">{
</span><span id="28" class="l">    <span class="php-keyword1">const</span> FORBID_JS_REGEX = <span class="php-quote">'(?!javascript:)'</span>;
</span><span id="29" class="l">    <span class="php-keyword1">const</span> PROTOCOL_REGEX = <span class="php-quote">'[a-z0-9-_]+(?::[a-z0-9-_]+)*://'</span>;
</span><span id="30" class="l">    <span class="php-keyword1">const</span> USER_REGEX = <span class="php-quote">'[a-z0-9-_]+(?::[a-z0-9-_]+)?@'</span>;
</span><span id="31" class="l">    <span class="php-keyword1">const</span> DOMAIN_REGEX = <span class="php-quote">'(?:[a-z0-9-_~]+\.)*[a-z0-9-_~]+(?::[0-9]{1,5})?/'</span>;
</span><span id="32" class="l">    <span class="php-keyword1">const</span> FOLDERS_REGEX = <span class="php-quote">'/*(?:[A-Za-z0-9~_\.+@,-]+/+)*'</span>;
</span><span id="33" class="l">    <span class="php-keyword1">const</span> FILE_REGEX = <span class="php-quote">'[A-Za-z0-9-+_,~:/\.\%!=]+'</span>;
</span><span id="34" class="l">    <span class="php-keyword1">const</span> ARGS_REGEX = <span class="php-quote">'(/([\w/_\.#-]*(\?)?(\S+)?[^\.\s])?)?'</span>;
</span><span id="35" class="l">
</span><span id="36" class="l">    <span class="php-keyword1">const</span> STATUS_OK = <span class="php-num">200</span>;
</span><span id="37" class="l">    <span class="php-keyword1">const</span> STATUS_FOUND = <span class="php-num">302</span>;
</span><span id="38" class="l">
</span><span id="39" class="l">    <span class="php-keyword1">private</span> <span class="php-keyword1">static</span> <span class="php-var">$root</span> = TPL_PATH_TO_ROOT;
</span><span id="40" class="l">    <span class="php-keyword1">private</span> <span class="php-keyword1">static</span> <span class="php-var">$server</span> = SERVER_URL;
</span><span id="41" class="l">
</span><span id="42" class="l">    <span class="php-keyword1">private</span> <span class="php-var">$url</span> = <span class="php-quote">''</span>;
</span><span id="43" class="l">    <span class="php-keyword1">private</span> <span class="php-var">$is_relative</span> = <span class="php-keyword1">false</span>;
</span><span id="44" class="l">    <span class="php-keyword1">private</span> <span class="php-var">$path_to_root</span> = <span class="php-quote">''</span>;
</span><span id="45" class="l">    <span class="php-keyword1">private</span> <span class="php-var">$server_url</span> = <span class="php-quote">''</span>;
</span><span id="46" class="l">
</span><span id="47" class="l">    <span class="php-comment">/**
</span></span><span id="48" class="l"><span class="php-comment">     * Build a Url object. By default, builds an Url object representing the current path.
</span></span><span id="49" class="l"><span class="php-comment">     * If the url is empty, no computation is done and an empty string will be returned
</span></span><span id="50" class="l"><span class="php-comment">     * when asking for both relative and absolute form of the url.
</span></span><span id="51" class="l"><span class="php-comment">     * @param string $url the url string relative to the current path,
</span></span><span id="52" class="l"><span class="php-comment">     * to the website root if beginning with a &quot;/&quot; or an absolute url
</span></span><span id="53" class="l"><span class="php-comment">     * @param string $path_to_root url context. default is PATH_TO_ROOT
</span></span><span id="54" class="l"><span class="php-comment">     */</span>
</span><span id="55" class="l">    <span class="php-keyword1">public</span> <span class="php-keyword1">function</span> __construct(<span class="php-var">$url</span> = <span class="php-quote">'.'</span>, <span class="php-var">$path_to_root</span> = <span class="php-keyword1">null</span>, <span class="php-var">$server_url</span> = <span class="php-keyword1">null</span>)
</span><span id="56" class="l">    {
</span><span id="57" class="l">        <span class="php-keyword1">if</span> (!<span class="php-keyword1">empty</span>(<span class="php-var">$url</span>))
</span><span id="58" class="l">        {
</span><span id="59" class="l">            <span class="php-keyword1">if</span> (<span class="php-var">$path_to_root</span> !== <span class="php-keyword1">null</span>)
</span><span id="60" class="l">            {
</span><span id="61" class="l">                <span class="php-var">$this</span>-&gt;path_to_root = <span class="php-var">$path_to_root</span>;
</span><span id="62" class="l">            }
</span><span id="63" class="l">            <span class="php-keyword1">else</span>
</span><span id="64" class="l">            {
</span><span id="65" class="l">                <span class="php-var">$this</span>-&gt;path_to_root = self::<span class="php-var">$root</span>;
</span><span id="66" class="l">            }
</span><span id="67" class="l">
</span><span id="68" class="l">            <span class="php-keyword1">if</span> (<span class="php-var">$server_url</span> !== <span class="php-keyword1">null</span>)
</span><span id="69" class="l">            {
</span><span id="70" class="l">                <span class="php-var">$this</span>-&gt;server_url = <span class="php-var">$server_url</span>;
</span><span id="71" class="l">            }
</span><span id="72" class="l">            <span class="php-keyword1">else</span>
</span><span id="73" class="l">            {
</span><span id="74" class="l">                <span class="php-var">$this</span>-&gt;server_url = self::<span class="php-var">$server</span>;
</span><span id="75" class="l">            }
</span><span id="76" class="l">
</span><span id="77" class="l">            <span class="php-var">$anchor</span> = <span class="php-quote">''</span>;
</span><span id="78" class="l">            <span class="php-keyword1">if</span> ((<span class="php-var">$pos</span> = TextHelper::<span class="php-keyword2">strpos</span>(<span class="php-var">$url</span>, <span class="php-quote">'#'</span>)) !== <span class="php-keyword1">false</span>)
</span><span id="79" class="l">            {
</span><span id="80" class="l">                <span class="php-comment">// Backup url arguments in order to restore them after compression</span>
</span><span id="81" class="l">                <span class="php-keyword1">if</span> (<span class="php-var">$pos</span> == <span class="php-num">0</span>)
</span><span id="82" class="l">                {
</span><span id="83" class="l">                    <span class="php-comment">// anchor to the current page</span>
</span><span id="84" class="l">                    <span class="php-var">$this</span>-&gt;url = <span class="php-var">$url</span>;
</span><span id="85" class="l">                    <span class="php-var">$this</span>-&gt;is_relative = <span class="php-keyword1">false</span>; <span class="php-comment">// forbids all url transformations</span>
</span><span id="86" class="l">                    <span class="php-keyword1">return</span>;
</span><span id="87" class="l">                }
</span><span id="88" class="l">                <span class="php-keyword1">else</span>
</span><span id="89" class="l">                {
</span><span id="90" class="l">                    <span class="php-var">$anchor</span> = TextHelper::<span class="php-keyword2">substr</span>(<span class="php-var">$url</span>, <span class="php-var">$pos</span>);
</span><span id="91" class="l">                    <span class="php-var">$url</span> = TextHelper::<span class="php-keyword2">substr</span>(<span class="php-var">$url</span>, <span class="php-num">0</span>, <span class="php-var">$pos</span>);
</span><span id="92" class="l">                }
</span><span id="93" class="l">            }
</span><span id="94" class="l">
</span><span id="95" class="l">            <span class="php-keyword1">if</span> (<span class="php-keyword2">preg_match</span>(<span class="php-quote">'`^[a-z0-9]+\:(?!//).+`iuU'</span>, <span class="php-var">$url</span>) &gt; <span class="php-num">0</span>)
</span><span id="96" class="l">            {   <span class="php-comment">// This is a special protocol link and we don't try to convert it.</span>
</span><span id="97" class="l">                <span class="php-var">$this</span>-&gt;url = <span class="php-var">$url</span>;
</span><span id="98" class="l">                <span class="php-keyword1">return</span>;
</span><span id="99" class="l">            }
</span><span id="100" class="l">            <span class="php-keyword1">else</span> <span class="php-keyword1">if</span> (TextHelper::<span class="php-keyword2">strpos</span>(<span class="php-var">$url</span>, <span class="php-quote">'www.'</span>) === <span class="php-num">0</span>)
</span><span id="101" class="l">            {   <span class="php-comment">// If the url begins with 'www.', it's an absolute one</span>
</span><span id="102" class="l">                <span class="php-var">$url</span> = <span class="php-quote">'http://'</span> . <span class="php-var">$url</span>;
</span><span id="103" class="l">            }
</span><span id="104" class="l">
</span><span id="105" class="l">            <span class="php-var">$url</span> = <span class="php-keyword2">preg_replace</span>(<span class="php-quote">'`^https?://'</span> . AppContext::get_request()-&gt;get_site_domain_name() . GeneralConfig::load()-&gt;get_site_path() . <span class="php-quote">'`uUi'</span>, <span class="php-quote">''</span>, self::compress(<span class="php-var">$url</span>));
</span><span id="106" class="l">            <span class="php-keyword1">if</span> (!TextHelper::<span class="php-keyword2">strpos</span>(<span class="php-var">$url</span>, <span class="php-quote">'://'</span>) &amp;&amp; TextHelper::<span class="php-keyword2">substr</span>(<span class="php-var">$url</span>, <span class="php-num">0</span>, <span class="php-num">2</span>) != <span class="php-quote">'//'</span>)
</span><span id="107" class="l">            {
</span><span id="108" class="l">                <span class="php-var">$this</span>-&gt;is_relative = <span class="php-keyword1">true</span>;
</span><span id="109" class="l">                <span class="php-keyword1">if</span> (TextHelper::<span class="php-keyword2">substr</span>(<span class="php-var">$url</span>, <span class="php-num">0</span>, <span class="php-num">1</span>) == <span class="php-quote">'/'</span>)
</span><span id="110" class="l">                {   <span class="php-comment">// Relative url from the website root (good form)</span>
</span><span id="111" class="l">                    <span class="php-var">$this</span>-&gt;url = <span class="php-var">$url</span>;
</span><span id="112" class="l">                }
</span><span id="113" class="l">                <span class="php-keyword1">else</span>
</span><span id="114" class="l">                {   <span class="php-comment">// The url is relative to the current folder</span>
</span><span id="115" class="l">                    <span class="php-var">$this</span>-&gt;url = <span class="php-var">$this</span>-&gt;root_to_local() . <span class="php-var">$url</span>;
</span><span id="116" class="l">                }
</span><span id="117" class="l">            }
</span><span id="118" class="l">            <span class="php-keyword1">else</span>
</span><span id="119" class="l">            {
</span><span id="120" class="l">                <span class="php-var">$this</span>-&gt;is_relative = <span class="php-keyword1">false</span>;
</span><span id="121" class="l">                <span class="php-var">$this</span>-&gt;url = <span class="php-var">$url</span>;
</span><span id="122" class="l">            }
</span><span id="123" class="l">            <span class="php-var">$this</span>-&gt;url = self::compress(<span class="php-var">$this</span>-&gt;url) . <span class="php-var">$anchor</span>;
</span><span id="124" class="l">        }
</span><span id="125" class="l">    }
</span><span id="126" class="l">
</span><span id="127" class="l">    <span class="php-comment">/**
</span></span><span id="128" class="l"><span class="php-comment">     * @return bool true if the url is a relative one
</span></span><span id="129" class="l"><span class="php-comment">     */</span>
</span><span id="130" class="l">    <span class="php-keyword1">public</span> <span class="php-keyword1">function</span> is_relative()
</span><span id="131" class="l">    {
</span><span id="132" class="l">        <span class="php-keyword1">return</span> <span class="php-var">$this</span>-&gt;is_relative;
</span><span id="133" class="l">    }
</span><span id="134" class="l">
</span><span id="135" class="l">    <span class="php-comment">/**
</span></span><span id="136" class="l"><span class="php-comment">     * Returns the root relative url if defined, else the absolute one
</span></span><span id="137" class="l"><span class="php-comment">     * @return string the root relative url if defined, else the absolute one
</span></span><span id="138" class="l"><span class="php-comment">     */</span>
</span><span id="139" class="l">    <span class="php-keyword1">public</span> <span class="php-keyword1">function</span> relative()
</span><span id="140" class="l">    {
</span><span id="141" class="l">        <span class="php-keyword1">if</span> (<span class="php-var">$this</span>-&gt;is_relative())
</span><span id="142" class="l">        {
</span><span id="143" class="l">            <span class="php-keyword1">return</span> <span class="php-var">$this</span>-&gt;url;
</span><span id="144" class="l">        }
</span><span id="145" class="l">        <span class="php-keyword1">else</span>
</span><span id="146" class="l">        {
</span><span id="147" class="l">            <span class="php-keyword1">return</span> <span class="php-var">$this</span>-&gt;absolute();
</span><span id="148" class="l">        }
</span><span id="149" class="l">    }
</span><span id="150" class="l">
</span><span id="151" class="l">    <span class="php-comment">/**
